import React, { Component } from "react";
import { Link } from "react-router-dom";
import {Button} from 'reactstrap';
import profile from '../../profile.png';

import axios from 'axios';

import {Col, Row, Container} from 'reactstrap';
export default class Profile extends Component {
    constructor() {
        super();
        this.state = {
          user: "",
          tempValue: 0,
        };      
        this.setWithdrawal = this.setWithdrawal.bind(this);
      }

      async setWithdrawal(e){
        e.preventDefault();
        let cartValue = this.getActualCartValue();
        this.state.user.data.saldo = cartValue;
        this.setState({
            tempValue: 0.0
        });        
      }

        getActualCartValue(){
            let actual = localStorage.getItem("cartValue");
            if (actual == null || actual == "null"){
                actual = 0;
                } else {
                actual = parseInt(actual);
                }  
            return actual;
        }

      async componentDidMount() {        
        const token = localStorage.getItem("token");

        /* Retorna as empresas existentes */
        const user = await fetch("http://localhost:3333/users/actual", {
          headers: new Headers({ Authorization: `Bearer ${token}` })
        })
          .then(response => {              
            return response.json();
          })
          .catch(err => console.log(err));   
        let cartValue = this.getActualCartValue();               
        this.setState({
            user,
            tempValue: cartValue
        });    
      }
    render() {                
        return (

            <Container className="Profile">
                <div className="header_profile">
                    <Link to="/home" className="link_profile float-left">Back</Link>
                    <Link to="/logout" className="link_profile float-right">Logout</Link>
                </div>   
                {this.state.user?
                <div className="body_profile">
                        <div>
                            <Link to="/profile">
                                <img src={profile} className="logoProfile_profile" alt="Logo" />
                            </Link>
                            <br></br>
                                <span className="email_profile">{this.state.user.data.email}</span>
                             <br></br>
                                <span className="phone_profile">{this.state.user.data.telefone}</span>

                        </div> 

                        <div>

                            <Row className="Info">                                                                
                                <Col xs="6" className="">
                                    <Col className="text-left">
                                        <span style={{fontSize: "1.3em"}}>Saldo Disponivel: </span><br></br>
                                        <span className="space-rigth"> R$ {this.state.user.data.saldo}</span>
                                        <Button color="success" type="submit"> Saque </Button>
                                    </Col>  
                                    <br></br>
                                    <Col className="text-left">
                                        <span style={{fontSize: "1.3em"}}> Saldo Pendente </span><br></br>
                                        <span className="space-rigth"> R${this.state.tempValue}</span>
                                        <Button color="warning" onClick={this.setWithdrawal}> Confirmar </Button>
                                    </Col>                                  
                                </Col>
                                <Col xs="6" className="card text-center">
                                    <span style={{fontSize: "1.5em", marginTop:"3vh"}}>Indique <br></br>e ganhe R$100</span>
                                    <Button color="info" > Compartilhe </Button>
                                </Col>
                            </Row> 
                        </div>
                </div>        
                :
                'hi'    
            }

            </Container>             
        );
    }
}
