import React, { Component } from 'react';
import { Row, Col } from 'reactstrap';
import { Link } from "react-router-dom";

import profile from '../../img/profile.png';

export default class Menu extends Component {
    // pegar o token
    // pegar o usuario logado atual
    //pegar o valor do saldo
    constructor() {
        super();
        this.state = {
          value: "",          
        };
      }

      async componentWillMount() {
        const token = localStorage.getItem("token");       

        /* Retorna os produtos existentes */
        const value = await fetch("http://localhost:3333/users/value", {
          headers: new Headers({ Authorization: `Bearer ${token}` })
        })
          .then(response => {
            return response.json();
          })
          .catch(err => console.log(err));

          this.setState(value);
        console.log(value);
      }
    render(){  

        return(            
                <div className="MenuHome">
                        <Row>
                            <Col xs="3"><h1 className="title">MASHUPFY</h1></Col>
                            <Col xs="3"></Col>                    
                            <Col xs="3"></Col>
                            <Col xs="3">
                                <div>
                                    <Link to="/profile">
                                        <img src={profile} className="logoProfile" alt="Logo" />
                                    </Link>
                                    <br></br>

                                        <span className="valueProfile"> R$ {this.state.data}</span>                        

                                </div>  
                            </Col>
                        </Row>      
                </div>
            )
        }
  }
